import {useSelector,useDispatch} from 'react-redux';
import {Helmet} from 'react-helmet'
import React, { useEffect} from "react";
import Footer from './Footer';
import {allposts} from '../redux';
import { Alert, ListGroup } from 'react-bootstrap'

export default function Home(){
    
  const {user}=useSelector(state=>state.AuthReducer);
  //const {allpost} = useSelector(state=>state.PostReducer);
  const dispatch = useDispatch();
  //dispatch(allposts());
  const {allpost} = useSelector(state=>state.PostReducer);
   
  console.log("all posts are",allpost)
  useEffect(()=>{
    dispatch(allposts());
  })
 
const title=user ?      <Helmet>
<meta charSet="utf-8" />
<title>Blog-Home</title>
</Helmet> :     <Helmet>
                <meta charSet="utf-8" />
                <title>Blog</title>
            </Helmet>;
    return (
      <>
   
        <div>
     {user? <h1>User logged in as: {user[0].uname}</h1> : <> </>}
     <ListGroup style={{ marginTop: "15px" }}>
      {  allpost ?
          allpost.map((err,i) => (
              <ListGroup.Item variant="danger" key={i} style={{ borderLeft: "3px solid #8c0000" }}>{allpost}</ListGroup.Item>
          ))
          : null
      }
    </ListGroup> 
        <h1>Home component</h1>

            
        </div>
        <Footer/>
        </>
    )
}
