const initialState={
    loading: false,
    signupErrors:[],
    signsuccess:'',
    loginError:[]
}
const AuthReducer=(state=initialState,action)=>{
   switch(action.type)
   {
       case SIGN_UP:return{
           ...state,
           loading:true
       }
       case SIGNUP_DONE:return{
           ...state,
           loading:false,
           signupErrors:[],
           signsuccess:action.payload
       }
       case SIGNUP_ERROR:return{
           ...state,
           loading:false,
           signupErrors:action.payload,
           signsuccess:''
       }
       default: return state;
   }
  
}
export default AuthReducer;
