import {SIGN_UP,SIGNUP_DONE,SIGNUP_ERROR,LOGIN,LOGIN_ERROR,LOGIN_DONE} from '../actions/Actiontypes.js';

import jwt_decode from "jwt-decode";
const initialState={
    loading: false,
    signupErrors:[],
    signsuccess:'',
    loginError:[],
    loginsuccess:'',
    token:'',
    user:''
}
const verifytoken=token=>{
    const decodedtoken=jwt_decode(token);
    console.log(decodedtoken);
    const expires=new Date(decodedtoken.exp*1000);
    if(new Date()> expires)
    {
    localStorage.removeItem('mytoken');

    }
    else
    {
        return decodedtoken;
    }
}
const token=localStorage.getItem('mytoken');
if(token)
{
    const decoded=verifytoken(token);
    initialState.token=token;
    const {email}=decoded;
    initialState.user=email;

}



const AuthReducer=(state=initialState,action)=>{
   switch(action.type)
   {
       case SIGN_UP:return{
           ...state,
           loading:true
       }
       case SIGNUP_DONE:return{
           ...state,
           loading:false,
           signupErrors:[],
           signsuccess:action.payload
       }
       case SIGNUP_ERROR:return{
           ...state,
           loading:false,
           signupErrors:action.payload,
           signsuccess:''
       }
       case LOGIN:return{
        ...state,
        loading:true

       }
       case LOGIN_DONE:return{
        ...state,
        loading:false,
        loginError:[],
        loginsuccess:action.payload

       }
       case LOGIN_ERROR:return{ 
           ...state,
        loading:false,
        loginError:action.payload,
        loginsuccess:''
    }
    case SET_TOKEN:
        const decoded=verifytoken(action.payload);
        const {email} =decoded;

        return{
            ...state,
        }
       default: return state;
   }
  
}
export default AuthReducer;
