import {Form,Button} from 'react-bootstrap'
import {useState} from 'react';
import {useDispatch} from 'react-redux';
import axios from 'axios';
export default function Signup()
{
  const [state,setState]=useState({
    uname:'',
    email:'',
    password:'',
    cpassword:''
  });
  const dispatch = useDispatch();
  const handleInput=(e)=>{
    setState({
      ...state,
      [e.target.name]:[e.target.value]

    });
  }
  const signing=async(e)=>{
    e.preventDefault();
    console.log(state);
    const config={
      headers:{
        "Content-type": "application/json"
      }
    };
    dispatch({type:'SET_LOADING'});
    try {
      const response=await axios.post("http://localhost:5000/signup",state,config);
      dispatch({type:'CLOSE_LOADING'});
      console.log(response);
      
    } catch (error) {
      dispatch({type:'CLOSE_LOADING'});

      console.log(error.response);
      
    }

  }
    return (
        <div className="container">
        <br/><br/>
        <h1>Signup</h1>
         <Form onSubmit={signing}>
         <Form.Group controlId="formGroupUsername">
         
    <Form.Label>Username</Form.Label>
    <Form.Control type="text" placeholder="Username" name="uname" onChange={handleInput} value={state.uname} />
  </Form.Group>
  <Form.Group controlId="formGroupEmail">
    <Form.Label>Email address</Form.Label>
    <Form.Control type="email" name="email" placeholder="Enter email" onChange={handleInput} value={state.email} />
  </Form.Group>
  <Form.Group controlId="formGroupPassword">
    <Form.Label>Password</Form.Label>
    <Form.Control type="password" name="password" placeholder="Password" onChange={handleInput} value={state.password}  />
  </Form.Group>
  <Form.Group controlId="formGroupcPassword">
    <Form.Label>Confirm Password</Form.Label>
    <Form.Control type="password" name="cpassword" placeholder="Confirm Password" onChange={handleInput} value={state.cpassword}  />
  </Form.Group>
  <Button variant="warning" type="submit">
    SignUp
  </Button>
</Form>
            
        </div>
    )
}
