import axios from 'axios';
import {SIGN_UP,SIGNUP_DONE,SIGNUP_ERROR} from './Actiontypes.js';

export const signingup=(user)=>{
    return function (dispatch){
        dispatch(signup())
        var config = {
            method: 'post',
            url: 'localhost:5000/signup',
            headers: { 
              'Content-Type': 'application/json'
            },
            data : user
          };
          
          axios(config)
          .then(function (response) {
            console.log(response);
            dispatch(signupdone(response.data.msg));
          })
          .catch(function (error) {
            console.log(error.response);
            dispatch(signuperror(error.response.data.errors))
          });
          

    }
}

const signup=()=>{
    return{

    
    type:SIGN_UP
    }

}
const signupdone=(success)=>{
    return {
        type:SIGNUP_DONE,
        payload: success
    }
  
}
const signuperror=(error)=>{
    return {
        type:SIGNUP_ERROR,
        payload: error
    }

}
