import axios from 'axios';
import {CREATE_POST,POST_ERROR,POST_SUCCESS} from './PostTypes.js';
const token=localStorage.getItem('mytoken');

export const creating=(post)=>{
      console.log()
      return function (dispatch){
          dispatch(create());
          var config = {
            method: 'post',
            url: 'localhost:5000/post/createpost',
            headers: { 
             Authorization: `Bearer ${token}`,
             
            },
            data : post
          };
         
          axios(config).then(function (response) { 
            console.log(response);
            dispatch(createdone(response.data.message));
          })
          .catch(function (error) {
            console.log(error.response);
            dispatch(createerror.response.data.errors);
          });


      }

}
const create=()=>{
    return{
    type:CREATE_POST
    }

}
const createdone=(success)=>{
    return {
        type:POST_SUCCESS,
        payload: success
    }
  
}
const createerror=(error)=>{
    return {
        type:POST_ERROR,
        payload: error
    }

}
