import {useSelector,useDispatch} from 'react-redux';
import {Helmet} from 'react-helmet'
import React, { useEffect} from "react";
import Footer from './Footer';
import {allposts} from '../redux';
import { Card,Button} from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUser,faEye } from '@fortawesome/free-solid-svg-icons'
export default function Home(){
    
  const {user}=useSelector(state=>state.AuthReducer);
 const {allpost} = useSelector(state=>state.PostReducer);
  const dispatch = useDispatch();
  //dispatch(allposts());
   
  //console.log("all posts are",allpost)
 useEffect(()=>{
   dispatch(allposts());
 },[])
  
 
const title=user ?      <Helmet>
<meta charSet="utf-8" />
<title>Blog-Home</title>
</Helmet> :     <Helmet>
                <meta charSet="utf-8" />
                <title>Blog</title>
            </Helmet>;
    return (
      <>
   
        <div>
     
 
    { allpost.length>0 ? 
                 
               
                 allpost.map((post)=>{ 
                    return ( 
                        <>
                        <Card style={{ width: '50%',margin: '30px'}} className="mx-auto">

                        <div style={{color:'red',height:'60px'}}><br/><b>UPLOADED BY:</b> <FontAwesomeIcon icon={faUser} />  {post.username}</div>
  <Card.Img variant="top" src={process.env.PUBLIC_URL + `/images/${post.image}` } style={{height: '350px'}} alt="mypic" />
  <Card.Body>
    <Card.Title>
   
    
    <br/><h1 style={{fontFamily: 'Noto Sans KR sans-serif'}}>{post.title}</h1></Card.Title>
    <hr/>
    <Card.Text>
    <h3>{post.description}</h3>

    </Card.Text>
    <Button variant="info" style={{margin:'20px'}}><FontAwesomeIcon icon={faEye} /> Click To See Post</Button>

   
  </Card.Body>
</Card>
           
           </>            
            )})

                :null
            }
           
      

            
        </div>
        <Footer/>
        </>
    )
}
