import {useSelector,useDispatch} from 'react-redux';
import {Helmet} from 'react-helmet'
import React, { useEffect,useState} from "react";
import Footer from './Footer';
import {allposts} from '../redux';
import { Card,Button} from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUser,faEye } from '@fortawesome/free-solid-svg-icons'
import moment from 'moment';
import "./style.css"

const renderdata=(allpost)=>{
  
    return ( 
      allpost.map((post)=>(
        <>
        <Card style={{ width: '50%',margin: '30px'}} className="mx-auto">

        <div ><br/>UPLOADED BY:   {post.username} <FontAwesomeIcon icon={faUser} /></div>
<Card.Img variant="top" src={process.env.PUBLIC_URL + `/images/${post.image}` } style={{height: '350px'}} alt="mypic" />
<Card.Body>
<Card.Title>

<span style={{color:'silver'}}>Published {moment(post.updatedAt).fromNow()}</span>
<br/><b><h1 style={{fontFamily: 'Noto Sans KR sans-serif'}}>{post.title}</h1></b></Card.Title>
<hr/>
<Card.Text>
<h3>{post.description}</h3>

</Card.Text>
<Button variant="info" style={{margin:'20px'}}><FontAwesomeIcon icon={faEye} /> Click To See Post</Button>


</Card.Body>
</Card>

</>   
      )         
))

}

export default function Home(){
    
  const {user}=useSelector(state=>state.AuthReducer);
 const {allpost} = useSelector(state=>state.PostReducer);
  const dispatch = useDispatch();
  //dispatch(allposts());
   
  //console.log("all posts are",allpost)
  const [currentPage,setcurrentPage]=useState(1);
    const [itemsPerPage,setitemsPerPage]=useState(3);
    const pages=[];
    for(let i=1;i<=Math.ceil(allpost.length/itemsPerPage);i++){
        pages.push(i);

    }
    const renderPage=pages.map(number=>{
        return (
            <li key={number} id={number}>{number}></li>
        )
    })
  const indexOfLastItem=currentPage*itemsPerPage;
  const indexOfFirstItem=indexOfLastItem-itemsPerPage;
  const currentItems=allpost.slice(indexOfFirstItem,indexOfLastItem);

 useEffect(()=>{
   dispatch(allposts());
 },[])
  
 
const title=user ?      <Helmet>
<meta charSet="utf-8" />
<title>Blog-Home</title>
</Helmet> :     <Helmet>
                <meta charSet="utf-8" />
                <title>Blog</title>
            </Helmet>;
    return (
      <>
   
        <div>
     <div className="pageNumbers"> {renderPage}</div>

    { allpost ? 
                 
               
                 renderdata(currentItems)

                :null
            }
            
           
        </div>
        <Footer/>
        </>
    )
}
