import {Form,Button,Container, Row, Col, Alert, ListGroup ,Card} from 'react-bootstrap'
import {Helmet} from 'react-helmet'
import {useDispatch,useSelector} from 'react-redux';
import {Redirect} from 'react-router-dom'
import React, { useState } from "react";
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import Footer from './Footer';

export default function CreatePost() {
    const {user}=useSelector(state=>state);
    const [image,setImage]=useState('Insert Image');
    const filechange=e=>{
      console.log(e.target.files[0].name);
      setImage(e.target.files[0].name);
    }
    //state for editor
    const [value, setValue] = useState('');
    const [state,setState] =useState({
      title:''
    })
    //state for slug
const [slug,setSlug]=useState('')

//state for slug update button
const [slugbutton,setslugbutton] = useState(false);

//getting button while updating slug
const slugupdate=(e)=>{
  setslugbutton(true);
  setSlug(e.target.value);
}

//slug update button onclick
const slugonclick=(e)=>{
  e.preventDefault();
  setSlug(slug.trim().split(' ').join('-'));
}

//this is for title while writing we will get slug
    const onchange=(e)=>{
      setState({
        ...state,
        [e.target.name]:e.target.value
      });
      const createslug=e.target.value.trim().split(' ').join('-');
      setSlug(createslug);
    }



    return (
        <>
        {user ? null :<Redirect to="/" /> }
            <Helmet>
                <meta charSet="utf-8" />
                <title>Create Post</title>
            </Helmet>
            <Container fluid>
            <Row>
            <Col xs={10} className="mx-auto">
            <Card style={{ marginTop:'30px'}}>
 
  <Card.Body>
    <Card.Title>CREATE NEW POST</Card.Title>
  <Row>
  <Col>
    <Form>
    <Form.Group as={Row} controlId="formPlaintext">
    <Form.Label column sm="2" >
      Title
    </Form.Label>
    <Col sm="10">
      <Form.Control type="text" onChange={onchange} value={state.value} placeholder="Enter Post Title" name="title" />
    </Col>
  </Form.Group>

  <div className="custom-file" style={{width:'70%',marginLeft:'35px',cursor:'pointer'}}>
    <input
      type="file"
      className="custom-file-input"
      id="inputGroupFile01"
      aria-describedby="inputGroupFileAddon01"  onChange={filechange} 
    />
    <label className="custom-file-label"  style={{backgroundColor: "#E2E0EC"}}htmlFor="inputGroupFile01">
      {image}
    </label>
  </div>

<br/>
<br/>
<Form.Group as={Row} controlId="formPlaintext">
    <Form.Label column sm="2">
      Body
    </Form.Label>
    <Col sm="10">
    <ReactQuill theme="snow" value={value} onChange={setValue}/>

    </Col>
  </Form.Group>
 
  <Form.Group as={Row} controlId="formPlaintext">
  <Form.Label column sm="2">

  </Form.Label>
  <Col sm="10">
  <Button variant="info" style={{width:'100%'}} className="justify-content-lg-center">Create Post</Button>

    </Col>

  </Form.Group>
</Form>
</Col>
<Col>
<Form.Group as={Row} controlId="formPlaintext">
    <Form.Label column sm="2" >
     Post URL
    </Form.Label>
    <Col sm="10">
      <Form.Control type="text" value={slug} onChange={slugupdate} placeholder="Post URL...." name="slug" />
    </Col>
  </Form.Group>
  {slugbutton ? <Form.Group as={Row} controlId="formPlaintext">
  <Form.Label column sm="2">

  </Form.Label>
  <Col sm="10">
  <Button variant="secondary" style={{width: '100%'}} onClick={slugonclick} className="justify-content-lg-center">Update Slug</Button>

    </Col>

  </Form.Group> : null}
  



</Col>
</Row>
<Col/>

<br/>


  
  </Card.Body>
</Card>

</Col >


</Row>
</Container>
<br/><br/>
<Footer/>
            
        </>
    )
}
