import React from 'react'
import {Navbar,Nav} from 'react-bootstrap'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faUser,faSignInAlt,faPen} from '@fortawesome/free-solid-svg-icons'
import {useDispatch,useSelector} from 'react-redux';
import { logout } from '../redux/actions/AuthAction'
export default function MyNavbar() {
  const {user}=useSelector(state=>state);
  const dispatch=useDispatch();
  const onclick=(e)=>{
    e.preventDefault();
    //console.log(state);
    localStorage.removeItem('mytoken');
    dispatch(logout());

  }
const links=user ? 
<>
<Nav.Link  href="/createpost"  style={{color:'black'}}>Create Post  <FontAwesomeIcon icon={faPen} /></Nav.Link>
<Nav.Link  href="/"  style={{color:'black'}}>{user[0].uname} <FontAwesomeIcon icon={faUser} /></Nav.Link>
<Nav.Link   onClick={onclick} style={{color:'black'}}>Logout <FontAwesomeIcon icon={faSignInAlt} /></Nav.Link>
</>
: 
<>
<Nav.Link  href="/signup"  style={{color:'black'}}>Signup <FontAwesomeIcon icon={faUser} /></Nav.Link>
<Nav.Link   href="/login"  style={{color:'black'}}>Login <FontAwesomeIcon icon={faSignInAlt} /></Nav.Link>
</>;
    return (
        
         <Navbar bg="light" variant="light" style={{backgroundColor:'white'}} className="myNavbar">
          {/* <img
        alt=""
        src={logo}
        width="30"
        height="30"
        className="d-inline-block align-top"
      /> */}
    <Navbar.Brand href="/"  style={{color:'black',fontSize:'39px',fontFamily:'RocknRoll One',fontWeight:'bold'}}>BLOG</Navbar.Brand>
    <Nav className="ml-auto"  >
      
      {links}
    </Nav>
 
  </Navbar>
        
            
        
    )
}
