import {CREATE_POST,POST_ERROR,POST_SUCCESS} from '../actions/PostTypes.js';

const initialState={
    loading:false,
    post_error:[],
    post_success:'',
    redirect:false

}

const PostReducer=(state=initialState,action)=>{
    switch(action.type)
    {
        case CREATE_POST:return{
            ...state,
            loading:true

        }

        case POST_SUCCESS:return{
            ...state,
            loading:false,
            post_error:[],
            post_success:action.payload
            

        }
        case POST_ERROR:return{
            ...state,
            loading:false,
            post_error:action.payload,
            post_success:''

        }
        case REMOVE_REDIRECT:return{

        }
        default:return state;
    }

}
export default PostReducer;
