import React from 'react'
import {useParams} from "react-router-dom";
import Footer from './Footer'
import { useEffect} from "react";
import {Helmet} from 'react-helmet'
import {Button,Card} from 'react-bootstrap'

import {useSelector,useDispatch} from 'react-redux';
import {showpost} from '../redux';
export default function ShowPost() {
    const {showPost} = useSelector(state=>state.PostReducer);
    const {user}=useSelector(state=>state.AuthReducer);
    let { slug } = useParams();
    const dispatch = useDispatch();
    useEffect(()=>{
       dispatch(showpost(slug));

    },[])
    console.log("show post console",showPost);

    return (
        <><Helmet>
        <meta charSet="utf-8" />
        <title>Show Post</title>
    </Helmet>
    {showPost.map((post)=>(
        <>
    <Card className="text-center">
  <Card.Header>{user ? <h1>{user[0].uname }<FontAwesomeIcon icon={faUser} /></h1> : post.username }</Card.Header>
  <Card.Body>
    <Card.Title>Special title treatment</Card.Title>
    <Card.Text>
      With supporting text below as a natural lead-in to additional content.
    </Card.Text>
    <Button variant="primary">Go somewhere</Button>
  </Card.Body>
  <Card.Footer className="text-muted">2 days ago</Card.Footer>
</Card>
</>
        ))}

  <Footer/>
            
        </>
        
    )
}
