import {Form,Button} from 'react-bootstrap'
import {useState} from 'react';
export default function Signup()
{
  const [state,setState]=useState({
    uname:'',
    email:'',
    password:'',
    cpassword:''
  });
  const handleInput=(e)=>{
    setState({
      ...state,
      [e.target.name]:[e.target.value]

    });
  }
    return (
        <div className="container">
        <br/><br/>
        <h1>Signup</h1>
         <Form>
         <Form.Group controlId="formGroupUsername">
    <Form.Label>Username</Form.Label>
    <Form.Control type="text" placeholder="Username" name="uname" onChange={handleInput} />
  </Form.Group>
  <Form.Group controlId="formGroupEmail">
    <Form.Label>Email address</Form.Label>
    <Form.Control type="email" name="email" placeholder="Enter email" onChange={handleInput}  />
  </Form.Group>
  <Form.Group controlId="formGroupPassword">
    <Form.Label>Password</Form.Label>
    <Form.Control type="password" name="password" placeholder="Password" onChange={handleInput}  />
  </Form.Group>
  <Form.Group controlId="formGroupcPassword">
    <Form.Label>Confirm Password</Form.Label>
    <Form.Control type="password" name="cpassword" placeholder="Confirm Password" onChange={handleInput}  />
  </Form.Group>
  <Button variant="warning" type="submit">
    SignUp
  </Button>
</Form>
            
        </div>
    )
}
