const initialState={
    loading: false,
    signupErrors:[],
    loginError:[]
}
const AuthReducer=(state=initialState,action)=>{
    if(action.type==='SET_LOADING')
    {
        return {
            ...state,loading:true
        }

    }
    else if(action.type=='CLOSE_LOADING')
    {
        return {
            ...state,loading:false
        }

    }
    else
    {
        return state;

    }
  
}
export default AuthReducer;
